import time, osfrom django.shortcuts import renderfrom django.template import loaderfrom django.conf import settingsfrom .models import ContentCategoryfrom .utils import get_categoriesdef generate_static_index_html():    """    生成静态的主页html文件    """    print('%s: generate_static_index_html' % time.ctime())    # 定义字典变量用来包装所有广告数据    contents = {}    # 查询广告类别所有数据    content_cat_qs = ContentCategory.objects.all()    # 遍历广告类别模型    for content_cat_model in content_cat_qs:        contents[content_cat_model.key] = content_cat_model.content_set.filter(status=True).order_by('sequence')    context = {        'categories': get_categories(),  # 所有商品类别数据        'contents': contents,  # 包装所有广告数据    }    # response = render(None, 'index.html', context)    # # # 获取响应对象中的响应体数据    # html_text = response.content.decode()    # 获取首页模板文件    template = loader.get_template('index.html')    # 渲染首页html字符串    html_text = template.render(context)    # 将首页html字符串写入到指定目录，命名'index.html'    file_path = os.path.join(settings.STATICFILES_DIRS[0], 'index.html')    with open(file_path, 'w', encoding='utf-8') as f:        f.write(html_text)